class Solution {
public:
     int dp[10][18000];
    int helper(int idx,int taken,vector<vector<int>>& student,vector<vector<int>>& mentor)
    {
        if(idx>=student.size()) return 0;
        if(dp[idx][taken]!=-1) return dp[idx][taken];
        int ans=-1e8;
        for(int i=0;i<student.size();i++)
        {
            //taken will tell us about the indexes that have been taken, e.g. taken=000101 in bit reprenstation tells index 0 and 2 have been taken
            if(taken&(1<<i)) continue; //if this index is already taken then continue
            taken^=(1<<i);             //take the current index ,xor will  set the bit  at index i
            int curr=0;
            for(int k=0;k<student[idx].size();k++){ //calculate current score
                if(student[idx][k]==mentor[i][k]) curr++;
            }
            ans=max(ans,(curr)+helper(idx+1,taken,student,mentor));//take maximum of all possibilities
            taken^=(1<<i); //backtrack the previous taken index, xor will reset the bit at index i
        }
        return dp[idx][taken]=ans;
    }
    int maxCompatibilitySum(vector<vector<int>>& student, vector<vector<int>>& mentor) {
        int n=student.size();int q=student[0].size();
        memset(dp,-1,sizeof(dp));
        int ans=helper(0,0,student,mentor);
        return ans;
    }
};
