class Solution {
public:

    int solve(vector<vector<int>>& s, vector<vector<int>>& m, int i, int j, int n, unordered_set<int>& st,int m2){
        if(i>=n || j>=n){
            return 0;
        }

        int ans = 0;
        for(int k = 0;k<n;k++){
            int temp = 0;
            if(st.find(k) == st.end()){
                int x = 0;
                for(int c = 0;c<m2;c++){
                    if(s[i][c] == m[k][c]){
                        x++;
                    }
                }
                st.insert(k);
                temp = x + solve(s,m,i+1,j,n,st,m2);
                st.erase(k);
            }
            ans = max(ans,temp);
        }
        return ans;
    }
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {

        int n = students.size(), m = mentors[0].size();
        unordered_set<int> st;
        return solve(students,mentors,0,0,n,st,m);
    }
};
