class Solution {
public:
    int ans ;
    map<pair<int ,int >, int >mm ; 
    int dp[1<<9][1<<9] ;
    int cal(int n, vector<vector<int>>& s , vector<vector<int>>& m , int m1 , int m2  )
    {
          if(m1==(1<<n)-1 && m2==(1<<n)-1)
          {
            return 0 ; 
          }

        if(dp[m1][m2]!=-1)
            return dp[m1][m2] ;

        int ans =0 , maxi = 0 ; 

        for(int i = 0 ;i<n ;i++ )
        {
            for(int j= 0; j<n ;j++ )
            {
                if((m1&(1<<i))==0 && (m2&(1<<j))==0)
                {
                  ans = mm[{i,j}] + cal(n , s, m ,m1|(1<<i) , m2|(1<<j)  );
                    maxi = max(maxi ,ans ) ;
                }
            }
        }
        return dp[m1][m2] = maxi ; 
    }   

    int maxCompatibilitySum(vector<vector<int>>& s , vector<vector<int>>& m ) {


        int n = s.size() ; 

          for(int i = 0 ;i<n ;i++ )
        {
            for(int j= 0; j<n ;j++ )
            {
                int ans = 0; 
             for(int k=0 ; k<s[i].size(); k++ )
             {
                 if(s[i][k]==m[j][k])
                     ans++ ; 
             }
                mm[{i, j}]= ans  ;
            }
        }
        memset(dp ,-1, sizeof(dp)) ;
        return cal(n, s , m , 0  , 0 ) ;

    }
};
