class Solution {
public:
    int findCompScore(vector<vector<int>>& students, vector<vector<int>>& mentors , int student , int mentor){
        int sum = 0;
        for (int i=0 ; i<students[0].size() ; i++){
            if (students[student][i] == mentors[mentor][i])
                sum++;
        }
        return sum;
    }

    void solve(vector<vector<int>>& students, vector<vector<int>>& mentors , vector<int>& vis, int student , int& res , int score){
        if (student >= students.size()){
            res = max(res , score);
            return;
        }

        for (int i=0 ; i<students.size() ; i++){
            if (vis[i] == 0){
                vis[i] = 1;
                int compatibility = findCompScore(students , mentors , student , i);
                solve(students , mentors , vis , student+1 , res , score + compatibility);
                vis[i] = 0;
            }
        }
    }

    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        vector<int> vis(mentors.size(),0);
        int index = 0;
        int res = 0;
        int score = 0;
        solve(students , mentors , vis , index , res , score);

        return res;
    }
};
