class Solution {
public:
    vector<bool> visitedMentors;
    unordered_map<string,int> mp;
    int compatibilityScore(vector<vector<int>>& students,vector<vector<int>>& mentors,int i,int j){
        int count=0;
        for(int k=0;k<students[i].size();k++){
            if(students[i][k]==mentors[j][k]){
                count++;
            }
        }
        return count;
    }
    int maxCompatibilitySumHelper(int i,vector<vector<int>>& students,vector<vector<int>>& mentors){
        if(i>=students.size()){
            return 0;
        }
        string key=to_string(i);
        for(int k=0;k<visitedMentors.size();k++){
            key+=to_string(visitedMentors[k]);
        }
        if(mp.find(key)!=mp.end()){
            return mp[key];
        }
        int maxCompatibility=0;
        for(int j=0;j<mentors.size();j++){
            if(!visitedMentors[j]){
                int score=compatibilityScore(students,mentors,i,j);
                visitedMentors[j]=1;
                int compatibility=score+maxCompatibilitySumHelper(i+1,students,mentors);
                maxCompatibility=max(maxCompatibility,compatibility);
                visitedMentors[j]=0;
            }
        }
        return mp[key]=maxCompatibility;
    }
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        int n=students.size();
        visitedMentors=vector<bool> (n,0);
        return maxCompatibilitySumHelper(0,students,mentors);
    }
};
