class Solution {
public:
    int maxCompatibilitySum(vector<vector<int>>& stu, vector<vector<int>>& men) {
        int n = stu.size();
        int m = stu[0].size();
        int dp[1 << (2 * n)];
        memset(dp, 0, sizeof(dp));
        int res = 0;
        for(int i = 0; i < n; i++)
        {
            int m1 = 1 << i;
            for(int j = 0; j < n; j++)
            {
                int m2 = 1 << j;
                int cnt = 0;
                for(int k = 0; k < m; k++)cnt += (stu[i][k] == men[j][k]);
                int mask = (m1 << n) | (m2);
                dp[mask] = max(dp[mask], cnt);
                res = max(dp[mask], res);
            }
        }
        for(int i = (1 << n) + 1; i < (1 << (2 * n)); i++)
        {
            if(__builtin_popcount(i) & 1)continue;
            for(int j = 0; j < n; j++)
            {
                for(int k = 0; k < n; k++)
                {
                    int m = 1 << (j + n) | (1 << k);
                    if(i & m)continue;
                    dp[i | m] = max(dp[i | m], dp[i] + dp[m]);
                    res = max(res, dp[i | m]);
                }
            }
        }
        return res;
    }
};
