class Solution {
public:
    int getCurScore(vector<int> &student, vector<int> &mentor) {
        int score = 0;
        for (int i = 0; i < student.size(); i++) {
            score += student[i] == mentor[i];
        }
        return score;
    }
    int assign(int student, int mask, vector<vector<int>>& students, vector<vector<int>>& mentors, vector<int> &dp) {
        // If all the students are assigned to each mentor we can't get further score
        if (student == students.size()) {
            return 0;
        }
        if (dp[mask] != -1) return dp[mask];

        int res = 0;
        // Try on all mentors to the current student and find optimal mentor.
        for (int mentor = 0; mentor < mentors.size(); mentor++) {
            // If we want to assign this mentor to current student if that bit is 0 and calculate cur score and call for next student.
            if (((1 << mentor) & mask) == 0) {
                int score = getCurScore(students[student], mentors[mentor]);
                res = max(res, score + assign(student + 1, mask | (1 << mentor), students, mentors, dp));
            }
        }
        return dp[mask] = res;
    }
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        // mask is to track which mentors are assigned till now and which are not since we can only assign mentor to only one student. (1 means assigned)
        int m = students.size();

        // of size 2^m since at the end we will assign all mentors so all m bits will set to 1.
        vector<int> dp(1 << m, -1);
        return assign(0, 0, students, mentors, dp); // [student, mask, [], []]
    }
};
