class Solution {
public:
    int ans = 0;
    void backtracking(int compatibility,int index,int m,int n,vector<vector<int>>&students,vector<vector<int>>&mentors,vector<bool>&bitmask){
        if(index<0){
            ans = max(ans,compatibility);
            return;
        }
        for(int i=0;i<m;i++){
            if(bitmask[i]) continue;
            bitmask[i] = true;
            int score = 0;
            for(int j=0;j<n;j++)
            {
                if(students[index][j]==mentors[i][j]) score++;
            }
            backtracking(compatibility+score,index-1,m,n,students,mentors,bitmask);
            bitmask[i] = false;
        }
    }
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        int m = students.size(); // no of students and mentors
        int n = students[0].size(); // subjects
        this->ans = 0;
        vector<bool>bitmask(m,false);
        backtracking(0,m-1,m,n,students,mentors,bitmask);
        return ans;
    }
};
