class Solution {
public:
    int comp(vector<int> &v1, vector<int> &v2){
        int ans=0;
        for(int i=0; i<v1.size(); i++){
            if(v1[i]==v2[i]){
                ans++;
            }
        }
        return ans;
    }


    void solve(int index,vector<vector<int>>& students, vector<vector<int>>& mentors,int bitmask,int count,int & ans){
         int n=mentors.size();
         if(bitmask==0 || index==n){
             ans=max(ans,count);
             return;
         }
         for(int j=0; j<n; j++){
          if(bitmask & (1<<j)){
          int temp=comp(students[index],mentors[j]);
          solve(index+1,students,mentors,bitmask^(1<<j),count+temp,ans);
           }
          }
    }


    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        int n=mentors.size();
        int bitmask=(1<<n)-1;
        int ans=0;
        int count=0;
        solve(0,students,mentors,bitmask,count,ans);
        return ans;
    }
};
