                        int newmask2 = mask2 | (1 << j);
                        int cnt = 0;
                        for(int k=0;k<n;k++){
                            if(students[i][k] == mentors[j][k]) cnt++;
                            count = Math.max(count,cnt + helper(students,mentors,newmask1,newmask2,dp,m,n));
                        }
                    }
                }
                score = Math.max(score,count);
            }
        }
        return dp[mask1][mask2] = score;
    }
}
