class Solution:
    def maxCompatibilitySum(self, students, mentors):
        m, n = len(students), len(students[0])

        def calc_comp_score(nums):
            total = 0

            for i in range(m):
                for j in range(n):
                    if students[i][j] == nums[i][j]:
                        total += 1

            return total

        def backtrack(i,path,visited):
            if i == m:
                self.res = max(self.res,calc_comp_score(path))
                return

            for j in range(m):
                if j not in visited:
                    visited.add(j)
                    backtrack(i+1,path+[mentors[j]],visited)
                    visited.remove(j)

        self.res = 0
        backtrack(0,[],set())
        return self.res












