class Solution {
public:

    int solve(int i,vector<vector<int>>& st, vector<vector<int>>& mt,vector<int>& visited)
    {
        if(i>=st.size())   //base condition
        {
            return 0;
        }
        int maxi=0;
        for(int j=0;j<mt.size();j++)
        {
            if(!visited[j])  //if mentor is not assigned then we will do operations
            {
                visited[j]=1;
                int match=0;
                for(int k=0;k<st[i].size();k++)
                {
                    if(st[i][k]==mt[j][k])
                    {
                        match++;
                    }
                }

                int score=match+solve(i+1,st,mt,visited);
                maxi=max(maxi,score);
                visited[j]=0;   //backtracking

            }
        }
        return maxi;
    }
    int maxCompatibilitySum(vector<vector<int>>& st, vector<vector<int>>& mt) {
        int n1=st.size();
        int m1=mt.size();
        vector<int>visited(m1,0); //repeated mentor can be avoided.
        return solve(0,st,mt,visited);

    }
};
