class Solution:
    def maxCompatibilitySum(self, students: List[List[int]], mentors: List[List[int]]) -> int:
        N,M = len(students),len(students[0])
		# transform the answers into mask
        st_mask, me_mask = [],[]
        for i in range(N):
            st_temp = me_temp = 0
            st,me = students[i],mentors[i]
            for j in range(M):
                st_temp |= (st[j] << j)
                me_temp |= (me[j] << j)
            st_mask.append(st_temp)
            me_mask.append(me_temp)

        # here we let student choose mentor
        full_mask = (1 << M) - 1    # here full_mask means all answers matched
        @cache
        def dp(idx,mask):
            if idx >= N:
                return 0
            score = 0
            for i in range(N):
                if (1 << i) & mask == 0:
                    score = max(score,dp(idx + 1,mask | (1 << i)) + 
                                (full_mask & ~(st_mask[idx] ^ me_mask[i])).bit_count())
            return score

        return dp(0,0)
