class Solution {
    int ans = 0;
    public int maxCompatibilitySum(int[][] students, int[][] mentors) {
        ArrayList<Integer> arr1 = new ArrayList<>();
        for(int i = 0 ; i < students.length ; i++){
            int num = 0;
            for(int j = 0 ; j < students[i].length ; j++){
                if(students[i][j] == 1){
                    num = num | (1 << j);
                }
            }
            arr1.add(num);
        }
        ArrayList<Integer> arr2 = new ArrayList<>();
        for(int i = 0 ; i < mentors.length ; i++){
            int num = 0;
            for(int j = 0 ; j < mentors[i].length ; j++){
                if(mentors[i][j] == 1){
                    num = num | (1 << j);
                }
            }
            arr2.add(num);
        }
        int n = mentors.length;
        int[][] dp = new int[n][257];
        for(int p = 0 ; p < n ; p++){
            for(int o = 0 ; o < 257 ; o++){
                dp[p][o] = -1;
            }
        }
        return getMax(0 , arr1 , arr2 , 0 , mentors[0].length , dp);
    }
    private int getMax(int i , ArrayList<Integer> arr1 , ArrayList<Integer> arr2 , int mask , int n , int[][] dp){
        if(i >= arr1.size()) return 0;
        if(dp[i][mask] != -1) return dp[i][mask];
        int ans = 0;
        for(int j = 0 ; j < arr2.size() ; j++){
            if((mask | (1 << j)) != mask){
                int newMask = (mask | (1 << j));
                ans = Math.max(get(arr1.get(i) , arr2.get(j) , n) + getMax(i+1 , arr1 , arr2 , newMask , n  , dp) , ans);

            }
        }
        return dp[i][mask] = ans;

    }
    private int get(int num1 , int num2 , int n){
        int xor = num1 ^ num2;
        int count = 0;
        while(xor > 0){
            count++;
            xor = xor & (xor-1);
        }
        //System.out.println(num1 + " " + num2 + " " + (n - count));
        return  n - count;
    }
}
