class Solution {
    func maxCompatibilitySum(_ s: [[Int]], _ m: [[Int]]) -> Int {

        let cs = s.map { s in m.map { zip(s, $0).filter({ $0 == $1 }).count } }
        var res = cs.indices.map({ cs[$0][$0] }).reduce(0, +)

        func bf(_ v: Int = 0, _ r: [Int] = Array(0..<m.count)) {

            guard !r.isEmpty else { return res = max(res, v) }
            let i = m.count - r.count

            for j in r.indices {
                var nr = r
                nr.remove(at: j)
                bf(v + cs[i][r[j]], nr)
            }
        }

        bf()

        return res
    }
}
