class Solution:
    def maxCompatibilitySum(self, students: List[List[int]], mentors: List[List[int]]) -> int:
        n = len(students)
        pairs = [[0 for _ in range(n)] for _ in range(n)]
        for i in range(n):
            for j in range(n):
                pairs[i][j] = sum([s == m for s, m in zip(students[i], mentors[j])])

        dp = [0 for _ in range(1<<n)]

        for mask in range(pow(2,n)):
            i = bin(mask).count("1")
            for j in range(n):
                if  (1<<j) & mask == 0:
                    dp[(1<<j) | mask ] = max(dp[(1<<j) | mask ], dp[mask]+pairs[i][j])


        return dp[(1<<n) -1]

```

