class Solution:
    def maxCompatibilitySum(self, students: List[List[int]], mentors: List[List[int]]) -> int:
        m = len(students)
        n = len(students[0])
        comp_dp = [[0 for i in range(m)] for j in range(m)]

        def comp(student, mentor):
            count = 0
            for an1, an2 in zip(students[student], mentors[mentor]):
                if an1 == an2:
                    count += 1
            return count

        @lru_cache(None)
        def dp(mask, mentor):
            ans = 0
            if mask == 0:
                return ans
            for i in range(m):
                if mask & (1 << i):
                    ans = max(ans, comp_dp[i][mentor] + dp(mask - (1 << i), mentor + 1))
            return ans

        for i in range(m):
            for j in range(m):
                comp_dp[i][j] = comp(i,j)
        return dp((1 << (m)) - 1, 0)
```

