class Solution {
    func maxCompatibilitySum(_ students: [[Int]], _ mentors: [[Int]]) -> Int {
        var result = 0
        var flag = [Int: Bool]()
        var index = 0
        let n = students.count
        let m = mentors.count
        let sum = 0
        f(index, &flag, students, mentors, n, m, sum, &result)
        return result
    }

    private func f(_ index: Int,
                    _ flag: inout [Int: Bool],
                    _ students: [[Int]],
                    _ mentors: [[Int]],
                    _ n: Int,
                    _ m: Int,
                    _ sum: Int,
                    _ result: inout Int) {
        if index == n {
            result = max(result, sum)
            return
        }

        for i in 0..<n {
            if flag[i] != true {
                flag[i] = true
                let temp = calculateCompatibility(index, i, students, mentors)
                f(index + 1, &flag, students, mentors, n, m, sum + temp, &result)
                flag[i] = false
            }
        }
    }

    private func calculateCompatibility(_ index: Int,
                                        _ j: Int,
                                        _ students: [[Int]],
                                        _ mentors: [[Int]]) -> Int {
        var sum = 0
        let student = students[index]
        let mentor = mentors[j]

        for i in 0..<student.count {
            if student[i] == mentor[i] {
                sum += 1
            }
        }
        return sum
    }
}
