class Solution {
public:
    #define ll long long
    ll dp[9][1LL<<8];

    ll solve(vector<vector<int>>&students,vector<vector<int>>&mentors,ll row,ll mask,ll n,ll m){
        if(row==n){
            return 0;
        }
        if(dp[row][mask]!=-1){
            return dp[row][mask];
        }
        ll answer=INT_MIN;
        for(ll i=0;i<n;i++){
            if(mask&(1LL<<i)){
                continue;
            }
            ll sum=0;
            for(ll j=0;j<m;j++){
                if(students[row][j]==mentors[i][j]){
                    sum++;
                }
            }
            answer=max(answer,sum+solve(students,mentors,row+1,mask|(1LL<<i),n,m));
        }
        return dp[row][mask]=answer;
    }

    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        ll n=students.size(),m=students[0].size();
        memset(dp,-1,sizeof(dp));
        return solve(students,mentors,0,0,n,m);
    }
};
