class Solution {
    fun maxCompatibilitySum(students: Array<IntArray>, mentors: Array<IntArray>): Int {
        val visited = BooleanArray(mentors.size)

        return maxCompatibilitySum(students, mentors, 0, visited)
    }

    fun maxCompatibilitySum(
        students: Array<IntArray>, 
        mentors: Array<IntArray>,
        sIndex: Int,
        visited: BooleanArray
    ): Int {
        if(sIndex == students.size) return 0

        var ans = 0
        for(i in 0 until mentors.size) {
            if(visited[i]) continue

            visited[i] = true
            ans = maxOf(
                ans,
                score(students[sIndex], mentors[i]) + maxCompatibilitySum(students, mentors, sIndex + 1, visited)
            )
            visited[i] = false
        }

        return ans
    }

    fun score(sAnswers: IntArray, mAnsers: IntArray): Int {
        var score = 0
        for(i in 0 until sAnswers.size) {
            if(sAnswers[i] == mAnsers[i]) {
                score++
            }
        }

        return score
    }
}
