class Solution {
public:
    int dp[8][256];
    int solve(int index,int mask,int n,int m,vector<int> &S,vector<int> &M){
		// if all pairing are done then return 0;
        if(mask==(1<<n)-1){
            return 0;
        }
        if(dp[index][mask]!=-1){
            return dp[index][mask];
        }
        int ans=0;
        for(int i=0;i<n;i++){
            if((mask&(1<<i))==0){
                int val1=S[index];
                int val2=M[i];
				// cnt calculate similarity between 2 valus
                int cnt=0;
                int k=0;
                while(k<m){
                    if((val1&(1<<k))==(val2&(1<<k))){
                        cnt++;
                    }
                    if((val1&(1<<k))){
                        val1=val1^(1<<k);
                    }
                    if((val2&(1<<k))){
                        val2=val2^(1<<k);
                    }                    
                    k++;
                }
				// maximize the similarity
                ans=max(ans,cnt+solve(index+1,mask|(1<<i),n,m,S,M));
            }
        }
        // cout<<index<<" "<<ans<<"\n";
        return dp[index][mask]=ans;
    }
    int maxCompatibilitySum(vector<vector<int>> &students, vector<vector<int>> &mentors) {
        int n=students.size();
        vector<int> S(n,0);
        vector<int> M(n,0);
        int m=students[0].size();
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
		 	  // convert 1,0 representation to binary number to reduceing size of 2d vector to 1d vector in recursion
                S[i]+=students[i][j]*pow(2,j);
                M[i]+=mentors[i][j]*pow(2,j);
            }
        }
        memset(dp,-1,sizeof dp);
        // vector<vector<int>> dp(n,vector<int>(1<<n,-1));
        int mask=0;
        return solve(0,mask,n,m,S,M);
    }
};
