class Solution {
public:
    int ans=INT_MIN, sum=0;

    int score(vector<int> &a, vector<int> &b){
        int res=0;
        for(int i=0; i<a.size(); i++){
            if(a[i]==b[i]) res++;
        }
        return res;
    }

    void helper(vector<vector<int>> &stu, vector<vector<int>> &ment, vector<bool> &vis, int i){

        if(stu.size()==i) return;

        for(int j=0; j<ment.size(); j++){
            if(!vis[j]){
                vis[j]=true;
                sum+=score(stu[i], ment[j]);
                helper(stu, ment, vis, i+1);
                ans=max(ans, sum);
                sum-=score(stu[i], ment[j]);
                vis[j]=false;
            }
        }

    }

    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        vector<bool> vis(mentors.size(), 0);
        helper(students, mentors, vis, 0);
        return ans;
    }
};
