class Solution {
public:
    int sum1=0,sum2=0;

    //function to match the student and mentor
    int score(vector<int>&a, vector<int>&b){
        int ans=0;
        for(int i=0; i<a.size(); i++){
            if(a[i]==b[i]) ans++;
        }
        return ans;
    }

    void solve(int i, vector<vector<int>>& students, vector<vector<int>>& mentors, vector<bool>&vis){
        if(i>=students.size()) return; //base condition

        for(int k=0; k<mentors.size(); k++){
            if( vis[k]==0){
                vis[k]=1;
                sum1+=score(students[i], mentors[k]); //add score to the sum
                solve(i+1,students,mentors, vis); //calculate for next
                sum2=max(sum2,sum1); //when reach end take maximum
                vis[k]=0; //backtrack
                sum1-=score(students[i], mentors[k]); //remove the scores
            }
        }
    }

    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        int n=students.size();
        vector<bool>vis(n,0);
        solve(0,students,mentors, vis);
        return sum2;
    }
};
