    struct Entry_s** table;
};

typedef struct Hashtable_s Hashtable;

int hash(Hashtable *hashtable, char *key) {
    unsigned long int hashvalue = 0;
    int idx = 0;
    while (hashvalue < ULONG_MAX && idx < strlen(key)) {
        hashvalue = hashvalue << 8;
        hashvalue += key[idx];
        idx++;
    }
    return hashvalue % hashtable->size;
}

Entry *createEntry(char *key, int value) {
    Entry *entry = (Entry *) malloc(sizeof(Entry));
    entry->key = strdup(key);
    entry->value = value;
    entry->next = NULL;
    return entry;
}

void set(Hashtable *hashtable, char *key, int value) {
    Entry *entry = createEntry(key, value);
    int hashvalue = hash(hashtable, key);
    if (hashtable->table[hashvalue] == NULL) {
        hashtable->table[hashvalue] = entry;
        hashtable->keys++;
    } else {
        Entry *current = hashtable->table[hashvalue];
        while (current != NULL) {
            if (strcmp(current->key, key) == 0) return;
            if (current->next == NULL) {
                current->next = entry;
                hashtable->keys++;
            }
            current = current-> next;
        }
    }
