    char *dd, *m, *c, transforms[100][50] = {""};
    int counter, i, j, trans_count = 0;

    // Populate the Morse transform array with an entry for each word
    for(i = 0; i < wordsSize; i++)
        setMorseTransform(words[i], transforms[i]);

    // Starting with first element, perform linear search through array for duplicates.
    // When a duplicate transform is found, copy the
    // last element in the transform array to position j, and decrement wordsSize.
    //
    // If the new element at transforms[j] is also a duplicate, repeat the process until
    // a non-duplicate is found or wordsSize is less than 2.
    //
    // The end result should be a de-duplicated array of transforms.

    for(i = 0; i < wordsSize; i++)
        for(j = i+1; j < (wordsSize); j++)
            while(j < wordsSize && wordsSize > 1 && !strcmp(transforms[i], transforms[j]))
            {
                strcpy(transforms[j], transforms[wordsSize - 1]);
                --wordsSize;
            }

    return wordsSize;
}
