int uniqueMorseRepresentations(char ** words, int wordsSize)
{
    // array containing morse code corresponding to indices 0:a, 1:b, 2:c ...
    char *morse[26] = {
        ".-","-...","-.-.","-..",".","..-.","--.","....","..",".---","-.-",".-..","--","-.","---",".--.","--.-",".-.","...","-","..-","...-",".--","-..-","-.--","--.."
    };

    // array to store morse code for each given words
    // max. length of an english character in morse code = 4
    // now, words[i].length <= 12
    // hence 12*4 = 48 + 1('\0') = 49(size of morse code of max. words[i].length)
    char store[wordsSize][49];

    // converting english characters to morse by accessing the morse array
    // english small letters start with ASCII 97, hence it is subtracted
    for(int i=0; i<wordsSize; i++)
    {
        strcpy(store[i], "");
        for(int j=0; words[i][j]!='\0'; j++)
            strcat(store[i], morse[words[i][j]-97]);
    }

    // counting the unique elements in the store array
    int ans=1, j=0;
    for(int i=1; i<wordsSize; i++)
    {
        for(j=0; j<i; j++)
            if(!strcmp(store[i], store[j]))
                break;
        if(i==j)
            ans++;
    }

    return ans;
}
```

