pub fn unique_morse_representations(words: Vec<String>) -> i32 {
	const TABLE: [&str; 26] = [
		".-", "-...", "-.-.", "-..", ".", "..-.", "--.", "....", "..", ".---", "-.-", ".-..", "--",
		"-.", "---", ".--.", "--.-", ".-.", "...", "-", "..-", "...-", ".--", "-..-", "-.--", "--..",
	];
	words
		.iter()
		.map(|w| w.chars().map(|c| TABLE[c as usize - 'a' as usize]).collect::<String>())
		.collect::<std::collections::HashSet<_>>()
		.len() as i32
}
