        int len=1;  // length of the prefix that matches the suffix
        if(dp[b][e]!=-1) return dp[b][e];
        for(int i=e; i>b; i--,len++){
            if(txt[b]==txt[i]){
                string suff = txt.substr(i,len);
                string pref = txt.substr(b,len);
				// if prefix and suffix are same, those two form a partition and then recursivley calculate for the inner subproblems. return the maximum value
                if(suff==pref){
                    res = 2+solve(txt,b+len,i-1);
                    break;
                } 
            }
        }
        return dp[b][e]=res;
    }

    int longestDecomposition(string text) {
        int n = text.size();
        memset(dp,-1,sizeof dp);
        int ans = solve(text,0,n-1);
        return ans;
    }
};
```


