class Solution {
    Map<String, Integer> map = new HashMap<>();
    public int longestDecomposition(String text) {
        if (map.containsKey(text)) return map.get(text);
        int len = text.length();
        if (len == 0) return 0;
        else if (len == 1) return 1;
        int count = 1;
        for (int i = 0; i <= len / 2 - 1; i++) {
            String sub = text.substring(0,  i + 1);
            if (text.startsWith(sub, len - i - 1)) {
                String s = text.substring(i + 1, text.length() - sub.length());
                count = Math.max(2 + longestDecomposition(s), count);
            }
        }
        map.put(text, count);
        return count;
    }
}
