    int dfs(const string &text, int l, int r) {
        if(r < l) return 0;
        if(l == r) return 1;
        if(dp[l][r] > 0) return dp[l][r];

        int n = r - l + 1;
        int ans = 1;
        for(int len = 1; len <= n / 2; len++) {
            auto left = text.substr(l, len);
