class Solution {
public:
    int longestDecomposition(string text) {
        int n = text.size();
        string left= "", right="";
        int count = 0, i=0;
        bool consumed;
        for(;i < n/2; i++){
            consumed = false;
            left = left + text[i];
            right= text[n-i-1] + right;
            if(left == right){
                count+=2;
                consumed = true;
                left = ""; right ="";
            }
        }

        return consumed && i == n/2 + n%2?count : count +1; 
    }
};
