class Solution {
public:
    unordered_map<string,int> mp;
    int solve(string &text){
        if(text=="") return 0;
        int ans=1;
        if(mp.find(text)!=mp.end()) return mp[text];
        for(int i=1;i<text.size();i++){
            string sub=text.substr(0,i);
            string rev=text.substr(text.size()-i,i);
            if(sub==rev){
                string news=text.substr(i,text.size()-2*i);
                ans=max(ans,2+solve(news));
            }
        }
        return mp[text]=ans;
    }

    int longestDecomposition(string text) {
        return solve(text);
    }
};
