class Solution {
public:
    int longestDecomposition(string& text) {

        if(text.length() == 0) return 1;

        int k = 0;
        int left = 0;
        int len = 1;
        int halfSize =text.length()/2;
        bool isZ = !(text.length() % 2);

        string_view ss =text;
        string_view firstStr;
        string_view secondStr;

        while (left + len <= halfSize)
        {
            firstStr = ss.substr(left, len);
            secondStr = ss.substr(text.length() - left - len, len);if(firstStr == secondStr)
            {
                k++;
                left += len;
                len = 1;
            }
            else
            {
                len++;
            }
        }
        k *=2;
        if(left != halfSize || !isZ) k++;

        return k;

    }
};
