class Solution:
    X, X_INV, MOD = 31, 129032259, 1000000007

    def longestDecomposition(self, text: str) -> int:
        n = len(text)
        prefixHash = [0] * (n + 1)
        xiInvs = [0] * n
        h, xi, xiInv = 0, 1, 1
        for i, c in enumerate(text):
            val = ord(c) - 96
            h = (h + xi*val) % Solution.MOD
            prefixHash[i] = h
            xiInvs[i] = xiInv
            xi = (Solution.X * xi) % Solution.MOD
            xiInv = (Solution.X_INV * xiInv) % Solution.MOD

        def equal(i, j):
            h1 = ((prefixHash[j] - prefixHash[i - 1]) * xiInvs[i]) % Solution.MOD
            ir, jr = n - i - 1, n - j - 1
            h2 = ((prefixHash[ir] - prefixHash[jr - 1]) * xiInvs[jr]) % Solution.MOD
            if h1 != h2:
                return False
            return all(text[i + idx] == text[jr + idx] for idx in range(j - i + 1))

        half = n >> 1
        res = i = j = 0
        while i < half:
            if j == half:
                return res + 1
            if equal(i, j):
                res += 2
                i = j + 1
            j += 1
        return res + (n & 1)




