class Solution {
public:

    int fun(string &s, int i)
    {
        int j=s.length()-1-i;
        int currLength=j-i+1;
        if(currLength<=1) return currLength;

        for(int len=1;len<=currLength/2;len++)
        {
            if(s.substr(i,len)==s.substr(j-len+1,len))
            {
                return 2+fun(s,i+len);
            }
        }
        return 1;
    }


    int longestDecomposition(string text) {
        //greedy
        return fun(text,0);
    }
};
