		if i > 0 {
			h[i] += h[i-1]
		}
	}

	var dp func(int, int) int
	dp = func(st int, end int) int {
		if end >= len(t) {
			return 0
		}
		if mem[st][end] != -1 {
			return mem[st][end]
		}
		cf := h[end]
		if st > 0 {
			cf -= h[st-1]
		}

		xend := len(t) - 1 - st
		xst := xend - (end - st)

		cs := h[xend]
		if xst > 0 {
			cs -= h[xst-1]
		}

		mn := xst - st
		tk := 0
		if mn < 0 {
			return 0
		}
		if cf*pPow[mn] == cs {
			a := 2
			if end >= len(t)/2 {
				a = 1
			}
			tk = a + dp(end+1, end+1)
		}
		ntk := dp(st, end+1)
		if ntk > tk {
			mem[st][end] = ntk
			return ntk
		}
		mem[st][end] = tk
		return tk
	}

	return dp(0, 0)
}
```

