const int                   N   = 10 + 1e3;

int     n;
int     Next[N];
int     f[N][N];


class Solution {
public:
    int longestDecomposition(string s) {
        n = s.size();
        s = "#" + s;

        memset(f, 0, sizeof(f));

        int k = 0;
        for (int l = n; l >= 1; l--) {          Next[l] = k = l-1;      f[l][l] = 1;
            for (int r = l+1; r <= n; r++) {                            f[l][r] = 1;
                while (k>=l && s[k+1] != s[r]) k = Next[k];
                if (s[k+1] == s[r]) k++;
                Next[r] = k;

                for (int i = k; i >= l; i = Next[i]) if ((i-l+1)*2 <= (r-l+1)){
                    f[l][r] = max(f[l][r], f[i+1][r-(i-l+1)] + 2);
                }
            }
        }
        return f[1][n];
    }
};

