class Solution:
    # This question asks for COMPLETE split
    # So, the strategy is to construct substring from two ends of the string
    # if see perfect match, we add 2 to the result
    # When both pointers reaches the end, we return 1
	# Edge case: text is empty, return 0
    def longestDecomposition(self, text: str) -> int:
        if not text:
            return 0
        l, r = 0, len(text)-1
        prefix, suffix, match = "", "", False 
        while l < len(text) and r >= 0 and not match:
            prefix += text[l]
            suffix = text[r] + suffix
            if prefix == suffix:
                match = True
            l += 1
            r -= 1
        if l == len(text) and r == -1:
            return 1
        else:
            return 2 + self.longestDecomposition(text[l:r+1])
