var longestDecomposition = function(text) {
    let l = 0, r = text.length - 1, rEnd = text.length;
    let output = 0;
    while (l <= r) {
        if (text[l] === text[r]) {
            if (l === r) {
                output++;
                break;
            }
            let tempL = l, tempR = r;
            // Check if l matches r all the way until rEnd
            // r must reach the rEnd, otherwise there is no valid match
            while (tempR < rEnd) {
                if (text[tempL] === text[tempR]) {
                    tempL++, tempR++;
                    continue;
                } else break;
            }
            // If matched, set l to its new position at tempL and set rEnd to r, thereby shrinking the window
            if (tempR === rEnd) {
                l = tempL;
                rEnd = r;
                output += 2;
            }
        }
        r--;
    }
    return output;
};
