class Solution {
    public int[] frequencySort(int[] nums) {
        HashMap<Integer,Integer> hm=new HashMap<>(); //Creating frequency map
        for(int x : nums)
        {
            if(hm.containsKey(x))
            {
                hm.put(x,hm.get(x)+1);
            }
            else
            {
                hm.put(x,1);
            }
        }
        Integer arr[]=new Integer[nums.length]; //since comparator cant be applied to primitive types
        for(int i=0;i<nums.length;i++)
        {
            arr[i]=nums[i]; //boxing
        }
        Arrays.sort(arr,new Comparator<Integer> ()
                    {
                        @Override
                        public int compare(Integer a , Integer b)
                        {
                            if(hm.get(a)> hm.get(b))
                                return 1;
                            else if(hm.get(a)<hm.get(b))
                                return -1;
                            else
                                return b-a;
                        }
                    });
        for(int i=0;i<nums.length;i++)
        {
            nums[i]=arr[i]; //unboxing
        }
        return nums;
    }
}
