    public int[] frequencySort(int[] nums) {
        HashMap<Integer,Integer> map = new HashMap<>();
        for(int i:nums) map.put(i,map.getOrDefault(i,0)+1);
        int[][] arr = new int[map.size()][2];
        int idx=0;
        for(int i:map.keySet()){
            arr[idx][0] = i;
            arr[idx++][1] = map.get(i);
