//Approach-1 (Priority_queue)
typedef pair<int, int> P;
class Solution {
public:
     //Comparator lambda function for priority_queue
    struct myFunction {
        bool operator() (P& p1, P& p2) {
            if(p1.second == p2.second)
                return p1.first < p2.first;

            return p1.second > p2.second;   
        }
    };
    vector<int> frequencySort(vector<int>& nums) {

        unordered_map<int, int> mp;

        int n = nums.size();
        for(int i = 0; i<n; i++) {
            mp[nums[i]]++;
        }
        priority_queue<P, vector<P>, myFunction> pq(mp.begin(), mp.end());

        vector<int> result;
        while(!pq.empty()) {
            int curr  = pq.top().first;
            int times = pq.top().second;
            while(times--)
                result.push_back(curr);
            pq.pop();
        }

        return result;

    }
};
