The idea of the third and fourth solutions are pretty similar to each other. The main difference is that in the third solution we use `sort.Slice`, while in the fourth solution we use a heap.


``` go
func frequencySort(nums []int) []int {
    m := make( map[int] int ) // map[val] index in arr
    arr := [][]int{}
    res := []int{}

    for _, num := range nums {
        if _, ok := m[num]; !ok {
            arr = append(arr, []int{})
            m[num] = len(arr) - 1
        }
        arr[m[num]] = append(arr[m[num]], num)
    }

    sort.Slice(arr, func(i, j int) bool {
        if len(arr[i]) == len(arr[j]) { return arr[i][0] > arr[j][0] }
        return len(arr[i]) < len(arr[j])
    })

    for _, a := range arr {
        res = append(res, a...)
    }

    return res
}
```

``` go
func frequencySort(nums []int) []int {
    m := make( map[int] []int )
    h := &IntHeap{}
    res := []int{}
    for _, num := range nums {
        m[num] = append(m[num], num)
    }

    for _, b := range m {
        heap.Push(h, b)
    }

    for h.Len() > 0 {
        res = append(res, heap.Pop(h).([]int)...)
    }

    return res
}

type IntHeap [][]int

func (h IntHeap) Len() int           { return len(h) }
func (h IntHeap) Less(i, j int) bool { 
    if len(h[i]) == len(h[j]) {
        return h[i][0] > h[j][0]
    }
    return len(h[i]) < len(h[j]) 
}
func (h IntHeap) Swap(i, j int)      { h[i], h[j] = h[j], h[i] }

func (h *IntHeap) Push(x interface{}) {
	*h = append(*h, x.([]int))
}

func (h *IntHeap) Pop() interface{} {
	old := *h
	n := len(old)
	x := old[n-1]
	*h = old[0 : n-1]
	return x
}
```

