impl Solution {
    pub fn frequency_sort(mut nums: Vec<i32>) -> Vec<i32> {

        // Count the frequency of each num
        let mut freq: HashMap<i32, u32> = HashMap::new();
        for e in &nums {
            *freq.entry(*e).or_insert(0) += 1;
        }

        nums.sort_by(|a,b| freq[a].cmp(&freq[b]).then(b.cmp(a)));
        nums        
    }
}
