import collections
class Solution:
    def frequencySort(self, nums: List[int]) -> List[int]:
        counter = collections.Counter(nums)
        buckets = [[0] * 201 for _ in range((len(nums) + 1))]
        for num, count in counter.items():
            buckets[count][num] = 1
        result = []
        for feq, bucket in enumerate(buckets):
            for i in reversed(range(-100, 101)):
                if bucket[i] > 0:
                    result += [i] * feq
        return result
