function frequencySort(nums: number[]): number[] {
    let map = new Map();
    let res = [];
    nums = nums.sort((a, b) => b - a);
    for(let i = 0; i < nums.length; i++){
        if(!map.has(nums[i])){
            map.set(nums[i], 1);
        } else {
            let count = map.get(nums[i]);
            map.set(nums[i], count + 1);
        }
    }

    new Map([...map.entries()].sort((a, b) => a[1] - b[1])).forEach((k, v) => {
        let x = Array(k).fill(v)
        res.push(x);
    });

    return res.flatMap(num => num);
};
