from heapq import *
class Solution:
    def frequencySort(self, nums: List[int]) -> List[int]:
        if len(nums)<=2:
            nums.sort(reverse=True)
            return nums
        dic=collections.Counter(nums)
        ans=[]
        res=[]
        h={}
        for i,j in dic.items():
            if j in h:
                h[j].append(i)
            else:
                h[j]=[i]
        l=[]
        for i,j in h.items():
            if len(h[i])>1:
                ans.extend([i,(i*j)])            

        if len(ans)>=1:
            for i in range(1,len(ans),2):
                ans[i].sort(reverse=True)
                l.extend([ans[i-1],ans[i]])
        heap=[]
        m=[]
        for i,j in dic.items():
            if j not in l:
                l.append([j,i])
        d=0
        while d<len(l):
            if type(l[d])==int:
                heappush(heap,[l[d],l[d+1]])
                d+=2
            else:
                heappush(heap,[l[d][0],l[d][1]])
                d+=1
        final=[]
        while heap:
            a=heappop(heap)
            if type(a[1])!=list and a[1]>=0:
                while a[0]!=0:
                    final.append(str(a[1]))
                    a[0]-=1
            elif type(a[1])!=list and a[1]<0:
                final.append([a[1]]*a[0])
            else:
                final.append(a[1])
        q=[]
        for i in final:
            if type(i)==int:
                q.append(i)
            elif type(i)==str:
                q.append(int(i))
            else:
                q.extend(i)
        return q



