            mp[it]++;
        }

        for(auto it: mp){
            pq.push({it.second,it.first});
        }

        nums.clear();
        while(!pq.empty()){
            int x = pq.top().first;
            int y = pq.top().second;
            while(x--){
                nums.push_back(y);
            }
            pq.pop();
        }
     return nums;
    }
};

