class Solution {
public:
    vector<int> frequencySort(vector<int>& nums) 
    {
        map<int,int>umap;
        map<int,vector<int>> map_freq;
        vector<int> final_vec;
        for(auto a:nums)
        {
            umap[a]++;//Saves numbers and their frequency
        }
        for(auto k:umap)
