class Solution:
    def frequencySort(self, nums: List[int]) -> List[int]:
        count = {}
        for num in nums:
            if num not in count:
                count[num] = 1
            else:
                count[num] += 1
        l = [(k,v) for k,v in count.items()]
        l.sort(key = lambda x:x[0], reverse=True)
        l.sort(key = lambda x:x[1])
        result = []
        for i in l:
            result.extend([i[0]]*i[1])
        return result

